dataSource.script:
select ACCOUNT_NUM, BILL_STATUS, BILL_SEQ,BILL_DTM,BILL_TYPE_ID,BALANCE_FWD_MNY,	INVOICE_NET_MNY, PAYMENTS_MNY,	ADJUSTMENTS_MNY,	REFUNDS_MNY,	BALANCE_OUT_MNY, INVOICE_NUM from billsummary@billing_system_dblink where ACCOUNT_NUM in (
select account_num from account@billing_system_dblink where customer_ref in (
select ca_number.value
from nc_params ca_number where ca_number.attr_id=2091641841013994123 and ca_number.object_id=${Customer_ID}
))

select ACCOUNT_NUM,	EVENT_SEQ,	EVENT_SOURCE,	EVENT_TYPE_ID,	EVENT_REF,	CREATED_DTM,	EVENT_DTM,	EVENT_COST_MNY,	MANAGED_FILE_ID,	TARIFF_ID,	EVENT_ATTR_1 as "Calling Number",	EVENT_ATTR_2 as "Called number",	EVENT_ATTR_3 as "Duration",	EVENT_ATTR_4 as "Direction",	EVENT_ATTR_5 as "Operator Code/Terminating Country",	EVENT_ATTR_6 as "OCS Charged Amount",	EVENT_ATTR_7,	EVENT_ATTR_8 as "Bucket Consumption amount",	EVENT_ATTR_9 as "Consumption type",	EVENT_ATTR_10,	EVENT_ATTR_11 as "Actual available bucket",	EVENT_ATTR_12 as "Served IMSI",	EVENT_ATTR_13 as "Terminating Zone",	EVENT_ATTR_14 as "Originating Country",	EVENT_ATTR_15 as "Originating Zone" from costedevent@billing_system_dblink where EVENT_TYPE_ID in ('1','2','3') and ACCOUNT_NUM in (select account_num from account@billing_system_dblink where customer_ref in (
select ca_number.value
from nc_params ca_number where ca_number.attr_id=2091641841013994123 and ca_number.object_id=${Customer_ID}
))

select ACCOUNT_NUM,	EVENT_SEQ,	EVENT_SOURCE,	EVENT_TYPE_ID,	EVENT_REF,	CREATED_DTM,	EVENT_DTM,	EVENT_COST_MNY,	MANAGED_FILE_ID,	TARIFF_ID,	EVENT_ATTR_1 as "Calling Number",	EVENT_ATTR_2 as "Called number",	EVENT_ATTR_3 as "Number of messages",	EVENT_ATTR_4 as "Direction",	EVENT_ATTR_5 as "Operator Code/Terminating Country",	EVENT_ATTR_6 as "OCS Charged Amount",	EVENT_ATTR_7,	EVENT_ATTR_8 as "Bucket Consumption amount",	EVENT_ATTR_9 as "Consumption type",	EVENT_ATTR_10,	EVENT_ATTR_11 as "Actual available bucket",	EVENT_ATTR_12 as "Served IMSI",	EVENT_ATTR_13 as "Terminating Zone",	EVENT_ATTR_14 as "Originating Country",	EVENT_ATTR_15 as "Originating Zone" from costedevent@billing_system_dblink where EVENT_TYPE_ID in ('4','6') and ACCOUNT_NUM in (select account_num from account@billing_system_dblink where customer_ref in (
select ca_number.value
from nc_params ca_number where ca_number.attr_id=2091641841013994123 and ca_number.object_id=${Customer_ID}
))

select ACCOUNT_NUM,	EVENT_SEQ,	EVENT_SOURCE,	EVENT_TYPE_ID,	EVENT_REF,	CREATED_DTM,	EVENT_DTM,	EVENT_COST_MNY,	MANAGED_FILE_ID,	TARIFF_ID,	EVENT_ATTR_1 as "Calling Number",	EVENT_ATTR_2 as "Called number",	EVENT_ATTR_3 as "Number of messages",	EVENT_ATTR_4 as "	Direction",	EVENT_ATTR_5,	EVENT_ATTR_6 as "OCS Charged Amount",	EVENT_ATTR_7,	EVENT_ATTR_8 as "Bucket Consumption amount",	EVENT_ATTR_9 as "Consumption type",	EVENT_ATTR_10 as "Actual available bucket",	EVENT_ATTR_11 as "Served IMSI",	EVENT_ATTR_12 as "Terminating Zone",	EVENT_ATTR_13,	EVENT_ATTR_14,	EVENT_ATTR_15 from costedevent@billing_system_dblink where EVENT_TYPE_ID in ('5') and ACCOUNT_NUM in (select account_num from account@billing_system_dblink where customer_ref in (
select ca_number.value
from nc_params ca_number where ca_number.attr_id=2091641841013994123 and ca_number.object_id=${Customer_ID}
))

select ACCOUNT_NUM,	EVENT_SEQ,	EVENT_SOURCE,	EVENT_TYPE_ID,	EVENT_REF,	CREATED_DTM,	EVENT_DTM,	EVENT_COST_MNY,	MANAGED_FILE_ID,	TARIFF_ID,	EVENT_ATTR_1 as "Originating Number",	EVENT_ATTR_2 as "Upload volume",	EVENT_ATTR_3 as "Download Volume",	EVENT_ATTR_4 as "Total Volume",	EVENT_ATTR_5 as "Rating Group",	EVENT_ATTR_6 as "OCS Charged Amount",	EVENT_ATTR_7,	EVENT_ATTR_8 as "Bucket Consumption amount",	EVENT_ATTR_9 as "Consumption type",	EVENT_ATTR_10,	EVENT_ATTR_11 as "Actual available bucket",	EVENT_ATTR_12 as "Served IMSI",	EVENT_ATTR_13 as "Roaming Country",	EVENT_ATTR_14 as "Roaming Zone",	EVENT_ATTR_15 from costedevent@billing_system_dblink where 
EVENT_TYPE_ID in ('7','8') and ACCOUNT_NUM in (select account_num from account@billing_system_dblink where customer_ref in (
select ca_number.value
from nc_params ca_number where ca_number.attr_id=2091641841013994123 and ca_number.object_id=${Customer_ID}
))
